```go
package main

import (
	"encoding/base64"
	"fmt"
	"os"

	"github.com/georgegebbett/pulumi-stripe/sdk/go/stripe"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
)

func filebase64OrPanic(path string) string {
	if fileData, err := os.ReadFile(path); err == nil {
		return base64.StdEncoding.EncodeToString(fileData[:])
	} else {
		panic(err.Error())
	}
}

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		// minimal file field
		_, err := stripe.NewFile(ctx, "logoFile", &stripe.FileArgs{
			Filename:      pulumi.String("logo.jpg"),
			Purpose:       pulumi.String("business_logo"),
			Base64content: filebase64OrPanic(fmt.Sprintf("%v/logo.jpg", HOME)),
		})
		if err != nil {
			return err
		}
		// file with file link
		_, err = stripe.NewFile(ctx, "logoIndex/fileFile", &stripe.FileArgs{
			Filename:      pulumi.String("logo.jpg"),
			Purpose:       pulumi.String("business_logo"),
			Base64content: filebase64OrPanic(fmt.Sprintf("%v/logo.jpg", HOME)),
			LinkData: &stripe.FileLinkDataArgs{
				Create:    pulumi.Bool(true),
				ExpiresAt: pulumi.Int(1826659124),
			},
		})
		if err != nil {
			return err
		}
		return nil
	})
}
