    businessProfile: {
        headline: "My special headline",
        privacyPolicyUrl: "https://example.com/privacy",
        termsOfServiceUrl: "https://example.com/terms",
    },
    defaultReturnUrl: "https://example.com/special_headline",
    features: {
        customerUpdate: {
            allowedUpdates: [
                "email",
                "address",
                "shipping",
                "phone",
                "tax_id",
            ],
            enabled: true,
        },
        invoiceHistory: {
            enabled: true,
        },
        paymentMethodUpdate: {
            enabled: true,
        },
        subscriptionCancel: {
            cancellationReason: {
                enabled: true,
                options: [
                    "too_expensive",
                    "missing_features",
                    "switched_service",
                    "unused",
                    "customer_service",
                    "too_complex",
                    "low_quality",
                    "other",
